var express=require("express");var app=express();var http=require("http").Server(app);const io=require("socket.io")(http);const PORT=process.env.PORT||7000;
app.get("/",function(b,a){a.sendFile(__dirname+"/index.html");});io.on("connection",function(a){a.on("message",function(b){console.log("message: "+b);io.emit("message",b);
});});http.listen(PORT,function(){console.log("server listening. Port:"+PORT);});